SELECT SUM("person_info"."id") as agg_0, MIN("cast_info"."id") as agg_1 FROM "person_info" LEFT OUTER JOIN "name" ON "person_info"."person_id" = "name"."id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id"  WHERE "person_info"."info" LIKE '%years%' AND "cast_info"."person_role_id" >= 1587736.0242245062 AND "aka_name"."name" NOT LIKE '%J%.%' AND "person_info"."info_type_id" <= 23;