SELECT COUNT(*) as agg_0, SUM("company_name"."id") as agg_1 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE "company_type"."kind" LIKE '%compani%es%' AND "company_name"."country_code" != '%[zm]%' AND "movie_companies"."company_type_id" = 2;