SELECT MIN("title"."kind_id") as agg_0, AVG("kind_type"."id") as agg_1 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE ("company_name"."country_code" LIKE '%[d%e]%' OR "company_name"."id" >= 118643) AND "movie_info"."info" NOT LIKE '%Portugu%ese%' AND "movie_companies"."id" BETWEEN 920299 AND 2052306 AND ("title"."id" BETWEEN 1024970 AND 1202535 OR "title"."title" LIKE '%and%' OR "title"."id" <= 2443537) AND "movie_companies"."note" LIKE '%(%worldwide)%';