SELECT SUM("company_type"."id") as agg_0, AVG("movie_info"."id") as agg_1 FROM "movie_info" LEFT OUTER JOIN "title" ON "movie_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id"  WHERE "cast_info"."role_id" != 2 AND "movie_companies"."company_type_id" >= 1 AND "title"."series_years" IN ('2006-????') AND "movie_keyword"."movie_id" IS NOT NULL AND "kind_type"."id" IS NOT NULL;