SELECT MAX("movie_keyword"."id") as agg_0, SUM("title"."episode_nr") as agg_1 FROM "title" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "title"."title" NOT LIKE '%Par%t%' AND "movie_keyword"."keyword_id" <= 3623 AND "kind_type"."id" BETWEEN 3 AND 4 AND "movie_companies"."note" LIKE '%(2005)%' AND "kind_type"."kind" IN ('tv mini series', 'movie', 'video game', 'video movie', 'tv series', 'episode');