SELECT AVG("title"."production_year") as agg_0, MIN("cast_info"."person_role_id") as agg_1 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id"  WHERE "cast_info"."note" NOT LIKE '%Adler)%' AND ("keyword"."phonetic_code" LIKE '%R1652%' OR ("keyword"."phonetic_code" LIKE '%R16%52%' AND "keyword"."phonetic_code" LIKE '%R1652%')) AND "movie_keyword"."movie_id" BETWEEN 1845539 AND 2072978;