SELECT AVG("title"."episode_nr") as agg_0 FROM "char_name" LEFT OUTER JOIN "cast_info" ON "char_name"."id" = "cast_info"."person_role_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE "char_name"."imdb_index" != '%I%' AND ("cast_info"."movie_id" <= 1730001 OR "cast_info"."person_id" >= 1222250) AND "char_name"."name_pcode_nf" NOT LIKE '%H524%1%' AND "keyword"."phonetic_code" NOT LIKE '%R1652%' AND "movie_keyword"."movie_id" >= 85597 AND "movie_info_idx"."movie_id" IS NOT NULL;