SELECT SUM("title"."episode_nr") as agg_0, MAX("title"."id") as agg_1 FROM "movie_companies" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE "movie_companies"."movie_id" <= 627498 AND "title"."season_nr" >= 1.7946496411609605 AND "movie_info"."id" BETWEEN 12138802 AND 14810924 AND "company_name"."country_code" LIKE '%[c%a]%' AND "movie_companies"."note" NOT LIKE '%(DVD%)%' AND "cast_info"."movie_id" IS NOT NULL AND "company_type"."id" IS NOT NULL;