SELECT AVG("movie_keyword"."movie_id") as agg_0, SUM("title"."kind_id") as agg_1 FROM "movie_info" LEFT OUTER JOIN "title" ON "movie_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id"  WHERE "title"."title" NOT LIKE '%de%' AND "movie_keyword"."id" <= 2118603 AND "movie_info_idx"."id" >= 1131572 AND "info_type"."info" NOT LIKE '%quality%' AND "movie_info"."info" LIKE '%Jap%anese%';