SELECT MIN("movie_info_idx"."info_type_id") as agg_0, SUM("movie_info"."info_type_id") as agg_1 FROM "info_type" LEFT OUTER JOIN "movie_info_idx" ON "info_type"."id" = "movie_info_idx"."info_type_id" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE "title"."imdb_index" IN ('X', 'II', 'IX', 'VII', 'III', 'I', 'V') AND ("info_type"."info" NOT LIKE '%country%' OR "info_type"."info" LIKE '%noise%') AND "movie_companies"."company_type_id" = 2;