SELECT MIN("movie_keyword"."id") as agg_0, MAX("title"."episode_of_id") as agg_1 FROM "movie_info" LEFT OUTER JOIN "title" ON "movie_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id"  WHERE ("movie_info"."info_type_id" BETWEEN 12 AND 16 OR "movie_info"."info_type_id" != 5) AND "kind_type"."id" BETWEEN 3 AND 5 AND "movie_info"."info" LIKE '%Po%rtuguese%' AND "keyword"."id" <= 3952 AND "movie_keyword"."movie_id" >= 181223;