SELECT COUNT(*) as agg_0, MAX("title"."kind_id") as agg_1 FROM "person_info" LEFT OUTER JOIN "name" ON "person_info"."person_id" = "name"."id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE ("aka_name"."id" >= 379660 OR "aka_name"."name" NOT LIKE '%Da%vid%' OR "aka_name"."id" >= 852248) AND "aka_name"."imdb_index" IN ('IV', 'III', 'I') AND "movie_companies"."note" NOT LIKE '%(200%7)%' AND "movie_info"."movie_id" IS NOT NULL;