SELECT COUNT(*) as agg_0 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE "movie_info_idx"."info" LIKE '%6.8%' AND "keyword"."phonetic_code" NOT LIKE '%R1652%' AND "movie_info_idx"."info_type_id" >= 100;