SELECT SUM("movie_info"."info_type_id") as agg_0 FROM "movie_info" LEFT OUTER JOIN "title" ON "movie_info"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE "kind_type"."kind" IN ('tv series', 'tv mini series') AND "movie_info"."movie_id" BETWEEN 1227889 AND 1617299 AND "movie_info"."info" NOT LIKE '%Germa%n%' AND "title"."series_years" IN ('2006-????', '2009-????') AND "movie_info_idx"."movie_id" IS NOT NULL;