SELECT AVG("movie_companies"."company_type_id") as agg_0 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id"  WHERE "company_type"."kind" NOT LIKE '%companies%' AND "cast_info"."note" NOT LIKE '%(uncredited)%' AND ("movie_companies"."movie_id" BETWEEN 1738717 AND 1909824 OR "movie_companies"."note" NOT LIKE '%(Germany)%') AND "char_name"."imdb_index" != '%M%' AND "kind_type"."id" IS NOT NULL;