SELECT MAX("aka_name"."person_id") as agg_0, COUNT(*) as agg_1 FROM "name" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id"  WHERE "name"."id" <= 3085588 AND "aka_name"."surname_pcode" IN ('J52', 'B2', 'B62', 'L2') AND ("aka_name"."name" LIKE '%A.%' OR ("aka_name"."name" NOT LIKE '%Brown%,%' AND "aka_name"."name" LIKE '%Brown,%')) AND "person_info"."person_id" IS NOT NULL AND "cast_info"."person_id" IS NOT NULL;