SELECT AVG("cast_info"."role_id") as agg_0, MAX("char_name"."id") as agg_1 FROM "char_name" LEFT OUTER JOIN "cast_info" ON "char_name"."id" = "cast_info"."person_role_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id"  WHERE ("cast_info"."movie_id" >= 1433741 OR ("cast_info"."movie_id" <= 34236 AND "cast_info"."movie_id" BETWEEN 1379441 AND 2305435)) AND ("aka_name"."person_id" <= 2007744 OR ("aka_name"."surname_pcode" LIKE '%B%42%' AND "aka_name"."person_id" <= 3989435)) AND "name"."surname_pcode" LIKE '%A2%6%' AND "title"."title" LIKE '%th%e%' AND "person_info"."person_id" IS NOT NULL;