SELECT COUNT(*) as agg_0, AVG("person_info"."id") as agg_1 FROM "name" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id"  WHERE "cast_info"."note" LIKE '%C%harlie%' AND "person_info"."info" LIKE '%have%' AND "person_info"."person_id" >= 2260791 AND "name"."surname_pcode" NOT LIKE '%B%4%' AND "char_name"."id" IS NOT NULL;