SELECT AVG("kind_type"."id") as agg_0, SUM("movie_companies"."company_id") as agg_1 FROM "char_name" LEFT OUTER JOIN "cast_info" ON "char_name"."id" = "cast_info"."person_role_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "aka_name"."id" BETWEEN 786852 AND 856025 AND "kind_type"."kind" LIKE '%series%' AND "cast_info"."id" >= 5495877 AND "movie_info"."info" NOT LIKE '%French%' AND "movie_companies"."note" LIKE '%(2007)%' AND ("cast_info"."movie_id" <= 2134951 OR "cast_info"."role_id" = 10) AND "movie_keyword"."movie_id" IS NOT NULL;