SELECT MIN("company_type"."id") as agg_0, MAX("movie_info_idx"."info_type_id") as agg_1 FROM "title" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id"  WHERE "movie_companies"."company_id" <= 243 AND ("title"."title" LIKE '%de%' OR "title"."title" NOT LIKE '%t%he%') AND "movie_companies"."note" LIKE '%(Can%ada)%' AND "company_type"."kind" IN ('distributors') AND "movie_info"."movie_id" IS NOT NULL AND "info_type"."id" IS NOT NULL;