SELECT AVG("kind_type"."id") as agg_0, AVG("cast_info"."nr_order") as agg_1 FROM "movie_info_idx" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id"  WHERE "keyword"."id" >= 127919 AND "cast_info"."role_id" != 1 AND "info_type"."info" != '%LD status of availablility%' AND ("title"."episode_of_id" IS NOT NULL OR "title"."series_years" = '%1995-1998%') AND "kind_type"."id" <= 5 AND "movie_info_idx"."info" LIKE '%7%.1%' AND "movie_info"."movie_id" IS NOT NULL AND "char_name"."id" IS NOT NULL AND "name"."id" IS NOT NULL;