SELECT MIN("cast_info"."role_id") as agg_0, AVG("cast_info"."person_id") as agg_1 FROM "info_type" LEFT OUTER JOIN "movie_info_idx" ON "info_type"."id" = "movie_info_idx"."info_type_id" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id"  WHERE "cast_info"."movie_id" >= 1071282 AND ("cast_info"."note" NOT LIKE '%f%ootage)%' OR ("cast_info"."note" NOT LIKE '%(uncredited)%' AND "cast_info"."note" LIKE '%(%as%')) AND "movie_info_idx"."info" LIKE '%7.4%' AND ("char_name"."name" NOT LIKE '%The%' OR "char_name"."name" NOT LIKE '%Ma%n%') AND "movie_info"."info" NOT LIKE '%Dutch%';