SELECT COUNT(*) as agg_0, MAX("cast_info"."person_role_id") as agg_1 FROM "person_info" LEFT OUTER JOIN "name" ON "person_info"."person_id" = "name"."id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id"  WHERE "cast_info"."note" NOT LIKE '%footag%e)%' AND "aka_name"."id" BETWEEN 187442 AND 477597 AND "char_name"."surname_pcode" LIKE '%M5%' AND "person_info"."info" LIKE '%(UK),%' AND "name"."surname_pcode" NOT LIKE '%B4%' AND "title"."id" IS NOT NULL;