SELECT MAX("char_name"."id") as agg_0 FROM "aka_name" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id"  WHERE ("aka_name"."surname_pcode" LIKE '%B63%' OR ("aka_name"."surname_pcode" IN ('M6') AND "aka_name"."surname_pcode" = '%N124%')) AND "title"."kind_id" >= 7 AND "title"."id" >= 805454 AND "movie_companies"."movie_id" IS NOT NULL;