SELECT MIN("movie_keyword"."movie_id") as agg_0, MAX("movie_info_idx"."info_type_id") as agg_1 FROM "title" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE "info_type"."info" NOT LIKE '%video%' AND "company_type"."id" BETWEEN 2 AND 3 AND "movie_companies"."note" LIKE '%(U%K)%' AND "movie_info"."info" NOT LIKE '%Dutc%h%' AND "kind_type"."id" IS NOT NULL;