SELECT MAX("movie_keyword"."id") as agg_0, AVG("cast_info"."person_role_id") as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id"  WHERE "keyword"."id" >= 133981 AND "cast_info"."note" LIKE '%f%ootage)%' AND "title"."title" NOT LIKE '%in%' AND "char_name"."id" IS NOT NULL;