SELECT AVG("movie_keyword"."keyword_id") as agg_0, MAX("title"."id") as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "movie_keyword"."id" <= 1076124 AND "kind_type"."id" BETWEEN 1 AND 3 AND ("movie_info_idx"."id" >= 1050980 OR "movie_info_idx"."movie_id" >= 2139077 OR "movie_info_idx"."id" >= 122435);