SELECT MAX("movie_companies"."company_type_id") as agg_0 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "title"."title" LIKE '%d%e%' AND "company_name"."name" NOT LIKE '%The%' AND ("kind_type"."kind" LIKE '%v%ideo%' OR "kind_type"."id" BETWEEN 4 AND 5 OR "kind_type"."kind" = '%tv series%');