SELECT MAX("movie_info_idx"."info_type_id") as agg_0, AVG("movie_keyword"."movie_id") as agg_1 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE "movie_companies"."id" <= 2234386 AND "keyword"."phonetic_code" NOT LIKE '%R1%652%' AND "movie_companies"."note" LIKE '%(Hunga%ry)%' AND "title"."title" LIKE '%the%';