SELECT MAX("movie_companies"."company_type_id") as agg_0, AVG("movie_companies"."company_type_id") as agg_1 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id"  WHERE "title"."episode_of_id" <= 769193.5423076253 AND "company_name"."country_code" NOT LIKE '%[in]%' AND "movie_companies"."note" LIKE '%(%2006)%' AND "title"."title" LIKE '%Th%e%';