SELECT SUM("movie_companies"."company_id") as agg_0 FROM "company_type" LEFT OUTER JOIN "movie_companies" ON "company_type"."id" = "movie_companies"."company_type_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE ("movie_companies"."note" LIKE '%(2012)%' OR ("movie_companies"."note" NOT LIKE '%(Hungary)%' AND "movie_companies"."note" NOT LIKE '%(Net%herlands)%')) AND "company_type"."id" >= 1 AND "company_type"."kind" LIKE '%compa%nies%' AND "movie_info_idx"."movie_id" IS NOT NULL;