SELECT AVG("aka_name"."id") as agg_0, SUM("title"."episode_nr") as agg_1 FROM "char_name" LEFT OUTER JOIN "cast_info" ON "char_name"."id" = "cast_info"."person_role_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id"  WHERE "name"."surname_pcode" NOT LIKE '%B26%' AND ("name"."name_pcode_cf" LIKE '%A4%162%' OR "name"."name_pcode_cf" LIKE '%A53%62%') AND "name"."surname_pcode" IN ('L2', 'J52', 'M6', 'B62', 'L', 'B65', 'S53', 'M2') AND "cast_info"."person_role_id" <= 624260.1271987229;