SELECT MAX("movie_companies"."company_type_id") as agg_0, AVG("movie_info"."info_type_id") as agg_1 FROM "company_type" LEFT OUTER JOIN "movie_companies" ON "company_type"."id" = "movie_companies"."company_type_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id"  WHERE "kind_type"."id" BETWEEN 1 AND 2 AND "movie_info"."info" NOT LIKE '%Spanish%' AND "company_name"."country_code" LIKE '%[in]%' AND "company_name"."id" >= 184885 AND "cast_info"."person_id" BETWEEN 1885747 AND 2851846;