SELECT MAX("title"."kind_id") as agg_0, AVG("title"."production_year") as agg_1 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE "title"."title" LIKE '%the%' AND "company_name"."id" <= 49818 AND "company_name"."country_code" IN ('[es]', '[us]') AND "kind_type"."id" IS NOT NULL AND "cast_info"."movie_id" IS NOT NULL AND "company_type"."id" IS NOT NULL;