SELECT AVG("title"."production_year") as agg_0, SUM("title"."episode_nr") as agg_1 FROM "movie_keyword" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id"  WHERE "movie_keyword"."id" BETWEEN 542449 AND 2521949 AND "cast_info"."role_id" BETWEEN 1 AND 2 AND "keyword"."id" BETWEEN 73537 AND 83383 AND ("title"."title" LIKE '%Par%t%' OR ("title"."title" NOT LIKE '%d%e%' AND "title"."title" NOT LIKE '%o%f%')) AND "cast_info"."id" >= 3130472 AND "keyword"."phonetic_code" LIKE '%R1652%' AND "kind_type"."id" IS NOT NULL;