SELECT AVG("movie_keyword"."id") as agg_0 FROM "movie_info" LEFT OUTER JOIN "title" ON "movie_info"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id"  WHERE "movie_keyword"."id" >= 2269527 AND "cast_info"."note" LIKE '%foo%tage)%' AND "movie_keyword"."keyword_id" <= 1857 AND "char_name"."id" IS NOT NULL;