SELECT COUNT(*) as agg_0, MIN("kind_type"."id") as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "movie_keyword"."movie_id" >= 1826065 AND "kind_type"."kind" NOT LIKE '%s%eries%' AND "title"."production_year" <= 1979.8030489049365 AND "movie_keyword"."keyword_id" <= 4658 AND ("kind_type"."kind" LIKE '%video%' OR ("kind_type"."id" BETWEEN 1 AND 2 AND "kind_type"."kind" LIKE '%vi%deo%')) AND "title"."title" NOT LIKE '%t%o%';