SELECT AVG("movie_companies"."company_id") as agg_0, AVG("company_name"."id") as agg_1 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE "company_name"."name" NOT LIKE '%TV%' AND "movie_companies"."company_id" <= 20552 AND "company_name"."id" <= 171598 AND "company_type"."id" IS NOT NULL;