SELECT MIN("title"."production_year") as agg_0, MIN("title"."kind_id") as agg_1 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id"  WHERE "movie_keyword"."movie_id" <= 2480008 AND "title"."imdb_index" IN ('III', 'II') AND "title"."title" NOT LIKE '%t%o%' AND "keyword"."phonetic_code" LIKE '%R16%52%' AND "keyword"."id" >= 53657;