SELECT COUNT(*) as agg_0 FROM "char_name" LEFT OUTER JOIN "cast_info" ON "char_name"."id" = "cast_info"."person_role_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id"  WHERE "info_type"."info" NOT LIKE '%o%ther%' AND ("char_name"."imdb_index" = '%DM%' OR ("char_name"."surname_pcode" NOT LIKE '%M5%' AND "char_name"."imdb_index" IN ('IV', 'II', 'M', 'I'))) AND "movie_info_idx"."movie_id" <= 2311972 AND "movie_info"."movie_id" IS NOT NULL;