SELECT SUM("title"."id") as agg_0 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "keyword"."phonetic_code" LIKE '%R%1652%' AND "movie_keyword"."movie_id" BETWEEN 2069043 AND 2376074 AND "kind_type"."id" BETWEEN 4 AND 5;