SELECT MIN("movie_companies"."company_id") as agg_0 FROM "movie_companies" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id"  WHERE "movie_info"."info_type_id" <= 13 AND "movie_info"."info" NOT LIKE '%Japanese%' AND "movie_info"."id" >= 8240095 AND "company_name"."id" IS NOT NULL AND "movie_keyword"."movie_id" IS NOT NULL;