SELECT MAX("title"."episode_of_id") as agg_0 FROM "movie_keyword" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id"  WHERE "title"."title" LIKE '%The%' AND "title"."imdb_index" != '%XIII%' AND ("movie_keyword"."keyword_id" BETWEEN 536 AND 1717 OR "movie_keyword"."keyword_id" BETWEEN 5584 AND 7457) AND "keyword"."id" IS NOT NULL;