SELECT SUM("movie_keyword"."keyword_id") as agg_0, AVG("movie_keyword"."movie_id") as agg_1 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE "movie_info_idx"."movie_id" >= 237856 AND "movie_info_idx"."info_type_id" = 101 AND "company_name"."name" LIKE '%Produc%tion%' AND "info_type"."id" IS NOT NULL AND "movie_info"."movie_id" IS NOT NULL AND "kind_type"."id" IS NOT NULL AND "company_type"."id" IS NOT NULL;