SELECT MAX("company_name"."id") as agg_0 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE ("movie_companies"."company_id" >= 115557 OR "movie_companies"."id" <= 222347 OR "movie_companies"."company_id" >= 72270) AND "movie_companies"."company_type_id" = 1 AND ("company_type"."kind" NOT LIKE '%companie%s%' OR "company_type"."kind" IN ('production companies', 'miscellaneous companies', 'distributors'));