SELECT MAX("cast_info"."nr_order") as agg_0, AVG("movie_info_idx"."movie_id") as agg_1 FROM "movie_info_idx" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "movie_info_idx"."info" LIKE '%7%.8%' AND "movie_keyword"."movie_id" >= 2317082 AND "char_name"."id" <= 2081214 AND ("cast_info"."note" LIKE '%foota%ge)%' OR ("cast_info"."id" BETWEEN 8953695 AND 22645384 AND "cast_info"."note" LIKE '%(voice)%'));