SELECT MIN("movie_info_idx"."movie_id") as agg_0, MAX("movie_companies"."company_id") as agg_1 FROM "aka_name" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE "name"."id" <= 3206039 AND "cast_info"."person_role_id" <= 222.86678255569302 AND "person_info"."id" >= 2311975 AND "name"."surname_pcode" LIKE '%B4%' AND "char_name"."id" IS NOT NULL AND "movie_info"."movie_id" IS NOT NULL;