SELECT COUNT(*) as agg_0 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id"  WHERE ("cast_info"."note" NOT LIKE '%footage)%' OR "cast_info"."note" NOT LIKE '%(voi%ce)%') AND "movie_companies"."note" LIKE '%(German%y)%' AND ("company_type"."kind" LIKE '%companies%' OR "company_type"."kind" NOT LIKE '%companies%' OR "company_type"."kind" NOT LIKE '%companies%') AND "company_name"."country_code" LIKE '%[fr]%';