SELECT COUNT(*) as agg_0 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id"  WHERE ("kind_type"."kind" NOT LIKE '%seri%es%' OR ("kind_type"."kind" IN ('tv movie', 'episode') AND "kind_type"."kind" != '%movie%')) AND ("movie_companies"."note" NOT LIKE '%(2005)%' OR ("movie_companies"."note" LIKE '%(Canada)%' AND "movie_companies"."note" NOT LIKE '%(Spai%n)%')) AND "title"."title" NOT LIKE '%and%' AND "keyword"."id" >= 4467 AND "kind_type"."id" = 3 AND "company_name"."country_code" IN ('[au]', '[jp]', '[in]', '[fr]', '[gb]', '[ca]', '[es]', '[de]');