SELECT MAX("kind_type"."id") as agg_0, MAX("title"."production_year") as agg_1 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE "movie_info"."id" <= 12608151 AND "movie_companies"."company_id" >= 6628 AND "company_name"."name" LIKE '%The%' AND "company_type"."id" IS NOT NULL AND "movie_info_idx"."movie_id" IS NOT NULL;